xwikidoc.content:
// {{groovy}}
import org.apache.commons.httpclient.Credentials;
import org.apache.commons.httpclient.HttpClient;
import org.apache.commons.httpclient.UsernamePasswordCredentials;
import org.apache.commons.httpclient.NameValuePair;
import org.apache.commons.httpclient.auth.AuthScope;
import org.apache.commons.httpclient.methods.PostMethod;
import org.apache.commons.httpclient.methods.StringRequestEntity;
import org.apache.commons.io.IOUtils;

class CorePlatformAPI {
  String startSimulation(modelId, currentUser, potentialUsers) {
    HttpClient httpClient = new HttpClient();
    httpClient.getParams().setAuthenticationPreemptive(true);
    AuthScope authentication = new AuthScope('localhost', 8080, AuthScope.ANY_REALM);
    Credentials credentials = new UsernamePasswordCredentials('superadmin', 'LearnPAss');
    httpClient.getState().setCredentials(authentication, credentials);
    String uri = String.format("http://localhost:8080/xwiki/rest/learnpad/cw/corefacade/simulation/start/%s", modelId);
    PostMethod postMethod = new PostMethod(uri);
    postMethod.addRequestHeader("Content-Type", "application/json");
    NameValuePair[] queryString = new NameValuePair[1];
    queryString[0] = new NameValuePair("currentuser", "superadmin");
    postMethod.setQueryString(queryString);
    StringRequestEntity requestEntity = new StringRequestEntity("[]", "application/json", "UTF-8");
    postMethod.setRequestEntity(requestEntity);
    httpClient.executeMethod(postMethod);
    return IOUtils.toString(postMethod.getResponseBodyAsStream());
  }
}
// {{/groovy}}
